import * as React from 'react'
import Navigation from './Navigation'
import Head from 'next/head'
// @ts-ignore
import RetinaImage from 'react-retina-image'

type LayoutProps = {
    title?: string
}

const Layout: React.FunctionComponent<LayoutProps> = ({ children, title }) => (
    <div>
        <link
            rel="stylesheet"
            type="text/css"
            href="//fonts.googleapis.com/css?family=Tenor+Sans"
        />
        <Head>
            <title>SAINT FAME</title>
            <meta
                name="viewport"
                content="initial-scale=1.0, width=device-width"
            />
        </Head>
        <span className="site">
            <span className="site-nav">
                <Navigation />
            </span>
            <span className="site-content">
                <span className="content-header flex-center">
                    <RetinaImage
                        src={require('../public/assets/logo/logo.png')}
                        style={{ marginTop: 24 }}
                    />
                </span>
                <span className="site-content">{children}</span>
            </span>
        </span>

        <style global jsx>{`
            body {
                background: black;
                color: #fff;
                font-family: Tenor Sans;
            }
            a {
                font-family: Tenor Sans;
                font-style: normal;
                font-weight: normal;
                font-size: 25px;
                line-height: 40px;

                color: #fffafa;
            }
            h1 {
                color: #fff;
            }

            // # PAGE STRUCTURE
            .site {
                display: flex;
                min-height: 100vh;
                background: red;
            }

            .site-nav {
                background: #4e87ea;
                width: 300px;
                border: 1px solid red;
            }

            .site-content {
                background: #eee;
                flex: 1;
                display: flex;
                flex-direction: column;
            }
            .content-header {
                background: #fff;
                height: 40px;
                box-shadow: 0 2px 0 rgba(0, 0, 0, 0.15);
            }
            .content-topic {
                background: #fff;
                flex-grow: 1;
                margin: 30px 50px;
                box-shadow: 5px 5px 0 rgba(0, 0, 0, 0.15);
            }

            // # TITLES
            .flex-center {
                display: flex;
                justify-content: center;
                align-items: center;
            }
            .section-title {
                font-size: 1.5em;
                font-family: 'Open Sans', sans-serif;
                color: rgba(0, 0, 0, 0.25);
                text-align: center;
            }
        `}</style>
    </div>
)
export default Layout
